              #         }
              #     }else{
              #         if(!is.null(param(.Object)[[item]])){
              #             return(param(.Object)[[item]])
              #         }
              #     }
              # }

              lapply(type, function(t){
                  t1 <- match.arg(t,c("input","output","other"))
              })
              if("input" %in% type){
                  if(!is.null(input(.Object)[[item]])){
                      return(input(.Object)[[item]])
                  }
              }
              if("output" %in% type){
                  if(!is.null(output(.Object)[[item]])){
